export default function validation(input) {
    const error = {};

    const regexEmail = /\$+@\$+\.\s+/;

    const regexPassword = new RegExp("[0-9]")

    if (!regexEmail.test(input.email)){
        error.email = "Debe ingresar un email válido"
    };
    if(!input.email){
        error.email="El campo de correo electrónico es requerido";
    };
    if(input.email.length > 35){
        error.email ="Correo muy largo, debe tener menos de 35 caracteres.";
    };
    if(regexPassword.test(input.password)){
        error.password = "La contraseña debe tener al menos un número"
    };
    if(input.password.length < 6 || input.password.length > 10){
        error.password= 'La contraseña debe contener entre 6 y 10 carácteres'
    };
    return error;
}
