export const validation=({userData}) =>{
    const errors = {};

    if(!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test
    (userData.email)){
        errors.email = 'el email ingresado no es valido';
    }
    if(!userData.email){
        errors.email ='Se requiere un correo electronico' ;
    }
    if(userData.email.length > 35){
        errors.email = "El email debe tener menos de 35 caracteres";
    }
    if(!/.*\d+.*/.test(userData.password)){
        errors.password= 'La contraseña debe tener al menos un número';
    }
    if(userData.password.length < 6 || userData.password.length > 10){
        errors.password='la contraseña debe ser mayor a 6 y menor que 10 caracteres'
    }

    return errors;
  };
